 // Extract the 'answers' array from the incoming data.
// Based on your webhook output, it's nested under 'body.body'.
let combinedData = {};
const answers = $input.item.json.body.body.answers;
const finalCombinedData = {
    email: combinedData.email || '',
    name: combinedData.name || '',
    totalScore: combinedData.totalScore || 0,
    categoryName: combinedData.categoryName || '',
    actionPlanTitle: combinedData.actionPlanTitle || '',
    categoryDescription: combinedData.categoryDescription || '',
    categoryActionPoints: combinedData.categoryActionPoints || '',
    actionPlanContent: combinedData.actionPlanContent || '',
    aiPrompt: combinedData.aiPrompt || '' // Keep aiPrompt if needed later
};

// Initialize a variable to store the running total score.
let totalScore = 0;

// Loop through each 'answer' object within the 'answers' array.
for (const answer of answers) {
  totalScore += answer.answer_value;
}

// Define the output data for the next nodes in the workflow.
// We pass along the original user's email and name,
// and the newly calculated 'totalScore'.
return [{
  json: {
    email: $input.item.json.body.body.email, // Access email from body.body
    name: $input.item.json.body.body.name,   // Access name from body.body
    totalScore: totalScore
  }
}];

